const popup=document.querySelector(".popup");const popupClose=document.querySelector(".popup__close");const modalBookBtn=document.querySelector(".modal-book-btn");const popupBtn=document.querySelectorAll(".modal-btn");for(let p=0;p<popupBtn.length;p++){popupBtn[p].addEventListener("click",function(){popup.classList.add("popup-open")})}popup.addEventListener("click",function(){popup.classList.remove("popup-open")});popupClose.addEventListener("click",function(){popup.classList.remove("popup-open")});modalBookBtn.addEventListener("click",function(){popup.classList.remove("popup-open")});document.addEventListener("keydown",function(p){if(p.key==="Escape"){if(popup.classList.contains("popup-open")){popup.classList.remove("popup-open")}}});